import "./App.css";
import { useState } from "react";
import { Link } from "react-router-dom";

var io = require('socket.io-client')
var socket = io.connect('http://localhost:3001', {reconnect: true});
const App = () => {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");

  const joinRoom = () => {
	  if (username !== "" && room !== "") {
		socket.emit("join_room", room);

	  }
	};

  return (
    <div className="Homepage">
      <div className="homepage-menu">
        <img
          src={require("/Users/Admin/Unstable Unicorns/client/src/assets/logoUU.png")}
          alt="LOGO"
          width="400px"
          margin-top="10px"
          class="center"
        />
        <div className="joinChatContainer">
          <input
            type="text"
            placeholder="Steven"
            onChange={(event) => {
              setUsername(event.target.value);
            }}
          />
          <input
            type="text"
            placeholder="Room ID..."
            onChange={(event) => {
              setRoom(event.target.value);
            }}
          />
        </div>
        <div className="homepage-form">
          <Link to={`/load?roomCode=${room}`}>
            <button className="game-button green" onClick={joinRoom}>
              JOIN GAME
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default App;
