	const [username, setUsername] = useState("");
	const [room, setRoom] = useState(data.roomCode);
  
	socket.emit("join_room", room);
	const joinRoom = () => {
	  if (username !== "" && room !== "") {
		socket.emit("join_room", room);
		
	  }
	};
  

